mapper.select:
SELECT COUNT(*)
FROM PRODUCT
WHERE PRODUCTNAME = #{productName}

SELECT DETAILID, CATEGORYID, DETAIL
FROM CATEGORYDETAILS
WHERE CATEGORYID = #{categoryId}

mapper.update:
UPDATE PRODUCT
SET PRODUCTIMAGE = #{productImage},
    PRODUCTNAME  = #{productName},
    EA           = #{ea},
    PRICE        = #{price}
WHERE PID = #{pid}
